{
    "title": "Content Data",
    "type": "object",
    "required": [
        "SectionID",
        "Subtitle",
        "Title",
        "Slogan"
    ],
    "properties": {
        "SectionID": {
            "type": "string",
            "default": "MyPromo"
        },
        "Subtitle": {
            "type": "string",
            "default": "We are Unify"
        },
        "Title": {
            "type": "string",
            "default": "Business &amp; Corporation"
        },
        "Slogan": {
            "type": "string",
            "default": "Sed feugiat porttitor nunc, non dignissim <br> ipsum vestibulum in. Donec in blandit dolor."
        },
        "More": {
            "type": "string",
            "default": "Learn More"
        },
        "MoreURL": {
            "type": "string",
            "default": "#"
        },
        "Images": {
            "type": "array",
            "format": "grid",
            "title": "Images",
            "items": {
                "type": "object",
                "title": "Image",
                "properties": {
                    "Image": {
                        "type": "string",
                        "format": "pageImage",
                        "default": "/Unify263/one-pages/business/assets/img-temp/1600x1075/img1.jpg",
                        "options": {
                            "upload": {
                                "width": 1600,
                                "height": 1075,
                                "upload_handler": "uploadHandler",
                                "getPageConfig_handler": "getPageConfig"
                            }
                        },
                        "links": [
                            {
                                "href": "{{self}}"
                            }
                        ]
                    }
                }
            }
        }

    }
}

