{
    "title": "Content Data",
    "type": "object",
    "required": [
        "SectionID",
        "Subtitle",
        "Title"
    ],
    "properties": {
        "SectionID": {
            "type": "string",
            "default": "MyAbout"
        },
        "Title": {
            "type": "string",
            "default": "Our clients"
        },
        "Subtitle": {
            "type": "string",
            "default": "Who work with us"
        },
        "Items": {
            "type": "array",
            "format": "table",
            "title": "Items",
            "items": {
                "type": "object",
                "title": "Item",
                "properties": {
                    "Image": {
                        "type": "string",
                        "format": "pageImage",
                        "default": "/Unify263/one-pages/business/assets/img-temp/200x150/img1.jpg",
                        "options": {
                            "upload": {
                                "width": 200,
                                "height": 150,
                                "upload_handler": "uploadHandler",
                                "getPageConfig_handler": "getPageConfig"
                            }
                        },
                        "links": [
                            {
                                "href": "{{self}}"
                            }
                        ]
                    },
                    "ImageDesc": {
                        "type": "string",
                        "default": "Image Description"
                    }
                }
            }
        },

        "Content": {
            "type": "string",
            "default": "Sed eget aliquet nisl. Proin laoreet accumsan nisl non vestibulum. Donec molestie, lorem nec sollicitudin elementum<br> tristique senectus et netus et malesuada fames ac turpis egestas."
        },
        "More": {
            "type": "string",
            "default": "Project"
        },
        "MoreURL": {
            "type": "string",
            "default": "#"
        }
    }
}

